import React from 'react';
import axios from 'axios';
import ReactTable from 'react-table';
import './App.css';
import 'react-table/react-table.css';

class App extends React.Component {

  constructor(props){
    super(props)

    this.state = {
      clubs : []
    }
  }

  componentWillMount() {

    var self = this;
    axios.get('http://localhost:8080/clubs')
        .then(function (response) {
          var clubs = response.data;
          self.setState({clubs})
        })
        .catch(function (error) {
          console.log(error);
        });


  }

  render() {

      const columns = [
      {
          Header: 'Logo du club',
          accessor: 'logo'
      },
      {
          Header: 'Nom du club',
          accessor: 'nom'
      },
      {
          Header: 'Pays du club',
          accessor: 'pays'
      },
      {
          Header: 'Joueurs du club',
          accessor: items => {
              let output = [];
              _.map(items.joueurs, joueur => {
                  output.push(joueur.nom,joueur.prenom,joueur.poste,joueur.numero);
              });
              return output.join(', ');
          }
      },
      ]

      const data = this.state.clubs;

    return (
            <ReactTable
                columns={columns}
                data={data}
            />
    );
  }
}

export default App;
