import React from 'react';
import axios from 'axios';
import ReactTable from 'react-table';
import './App.css';
import 'react-table/react-table.css';

class App extends React.Component {

  constructor(props){
    super(props)

    this.state = {
      clubs : []
    }
  }

  componentWillMount() {

    var self = this;
    axios.get('http://localhost:8080/clubs')
        .then(function (response) {
          var clubs = response.data;
          self.setState({clubs})
        })
        .catch(function (error) {
          console.log(error);
        });


  }

  render() {

      const columns = [
      {
          Header: 'Logo du club',
          accessor: 'logo',
          Cell: logo => {
              return(
                  <div>{logo[0]}</div>
              )
          }
      },
      {
          Header: 'Nom du club',
          accessor: 'nom'
      },
      {
          Header: 'Pays du club',
          accessor: 'pays'
      },
      {
          Header: 'Joueurs du club',
          accessor: "joueurs",
          Cell: (joueurs) => {
              return (
                  <div>
                   {joueurs.value.map(joueur => (
                       <span>{joueur.nom}</span>
                   ))}
                  </div>
              )
          },
      },
      ]

      const data = this.state.clubs;

    return (
            <ReactTable
                columns={columns}
                data={data}
            />
    );
  }
}

export default App;
